import React from "react";
import TopNav from "../Components/TopNav";
import { FormErrors } from './FormErrors';

const Registration = (props) => {
  return (
    <div>
      <TopNav />
      <form className="demoForm">
        <h2>Sign up</h2>
        <div className="panel panel-default">
          <FormErrors formErrors={this.props.formErrors} />
        </div>
        <div className={`form-group ${this.props.errorClass(this.props.formErrors.email)}`}>
          <label htmlFor="email">Email address</label>
          <input type="email" required className="form-control" name="email"
            placeholder="Email"
            value={this.props.email}
            onChange={this.props.handleUserInput}  />
        </div>
        <div className={`form-group ${this.props.errorClass(this.props.formErrors.password)}`}>
          <label htmlFor="password">Password</label>
          <input type="password" className="form-control" name="password"
            placeholder="Password"
            value={this.props.password}
            onChange={this.props.handleUserInput}  />
        </div>
        <button type="submit" className="btn btn-primary" disabled={this.props.formValid}>Sign up</button>
      </form>
    </div>
  );
};
export default Registration;
